package me.tyjam.wildpvp;

import org.bukkit.plugin.java.JavaPlugin;

public class WildPVP extends JavaPlugin {
    private static WildPVP instance;

    @Override
    public void onEnable() {
        instance = this;
        this.getCommand("wildpvp").setExecutor(new WildPVPCommand());
        getServer().getPluginManager().registerEvents(new WildPVPListener(), this);
    }

    public static WildPVP getInstance() {
        return instance;
    }
}

package me.tyjam.wildpvp;

import org.bukkit.command.Command;
import org.bukkit.command.CommandExecutor;
import org.bukkit.command.CommandSender;
import org.bukkit.entity.Player;

public class WildPVPCommand implements CommandExecutor {
    @Override
    public boolean onCommand(CommandSender sender, Command command, String label, String[] args) {
        if (!(sender instanceof Player)) return true;
        Player player = (Player) sender;
        WildPVPQueueGUI.open(player);
        return true;
    }
}

package me.tyjam.wildpvp;

import java.util.ArrayList;
import java.util.Collections;
import java.util.List;
import java.util.UUID;

public class WildPVPQueue {
    private static final List<UUID> queue = new ArrayList<>();

    public static List<UUID> getQueue() {
        return Collections.unmodifiableList(queue);
    }

    public static boolean isQueued(UUID uuid) {
        return queue.contains(uuid);
    }

    public static void add(UUID uuid) {
        if (!queue.contains(uuid)) queue.add(uuid);
    }

    public static void remove(UUID uuid) {
        queue.remove(uuid);
    }
}

package me.tyjam.wildpvp;

import org.bukkit.Bukkit;
import org.bukkit.Material;
import org.bukkit.entity.Player;
import org.bukkit.inventory.Inventory;
import org.bukkit.inventory.ItemStack;
import org.bukkit.inventory.meta.ItemMeta;
import org.bukkit.inventory.meta.SkullMeta;

import java.util.List;
import java.util.UUID;
import java.util.stream.Collectors;

public class WildPVPQueueGUI {
    public static void open(Player player) {
        Inventory gui = Bukkit.createInventory(null, 9, "WildPVP Queue");
        List<UUID> queue = WildPVPQueue.getQueue();
        int i = 0;
        for (UUID uuid : queue) {
            if (i >= 8) break;
            Player p = Bukkit.getPlayer(uuid);
            if (p == null) continue;
            ItemStack skull = new ItemStack(Material.PLAYER_HEAD, 1);
            SkullMeta meta = (SkullMeta) skull.getItemMeta();
            meta.setOwningPlayer(p);
            meta.setDisplayName(p.getName() + (p.equals(player) ? " (You)" : ""));
            skull.setItemMeta(meta);
            gui.setItem(i, skull);
            i++;
        }
        // Join/Leave button
        ItemStack joinItem = new ItemStack(Material.EMERALD_BLOCK);
        ItemMeta joinMeta = joinItem.getItemMeta();
        if (queue.contains(player.getUniqueId())) {
            joinMeta.setDisplayName("§cLeave Queue");
        } else {
            joinMeta.setDisplayName("§aJoin Queue");
        }
        joinItem.setItemMeta(joinMeta);
        gui.setItem(8, joinItem);

        player.openInventory(gui);
    }
}
